import { useSelector } from 'react-redux';
import { Box } from '@mui/material';
import { useVegetableProducts } from '../../../hooks/useVegetableProducts';
import ProductCard from '../../../components/allProducts/ProductCard';
import Spinner from '../../../components/common/Spinner';
import {useEffect, useState} from "react";

const productCardsContainerStyle = {
  width: '100%',
  display: 'grid',
  '@media (max-width: 799px)': {
    gridTemplateColumns: 'auto',
  },
  '@media (min-width: 800px)': {
    gridTemplateColumns: 'fit-content(336px) fit-content(336px)',
  },
  '@media (min-width: 1200px)': {
    gridTemplateColumns:
      'fit-content(336px) fit-content(336px) fit-content(336px)',
  },
  justifyContent: 'center',
  gridGap: '32px',
  padding: '32px',
  paddingTop: 8,
  paddingBottom: 4,
  minWidth: '320px',
};

const ProductCardsContainer = () => {
  const pageNumber = useSelector((state) => state.allProducts.pageNumber);

  const vegetablesProducts = useVegetableProducts(pageNumber);

  if (vegetablesProducts.isLoading) {
    return <Spinner />;
  }

  if (vegetablesProducts.isError) {
    return null;
  }

  return (
    <Box sx={productCardsContainerStyle}>
      {vegetablesProducts.data?.content.map((product) => (
        <ProductCard
          key={product.id}
          product={product}
          numberOfElements={vegetablesProducts.data?.numberOfElements}
        />
      ))}
    </Box>
  );
};

export default ProductCardsContainer;
