import SearchIcon from '@mui/icons-material/Search';
import {
  Box,
  Button,
  InputBase,
  InputAdornment,
  IconButton,
} from '@mui/material';
import {useEffect, useState} from 'react';
import CloseIcon from '@mui/icons-material/Close';
import {setFilters} from "../../../redux/slices/allProducts";

const inputSearchContainerStyle = { display: 'flex', flexGrow: 1 };

const inputBaseContainerStyle = {
  flexGrow: 1,
  border: '1px solid rgba(0,0,0,0.2)',
  transition: 'all 0.3s linear',
  '&:hover': {
    border: '1px solid',
    borderColor: 'primary.main',
  },
};

const inputBaseStyle = {
  padding: 1,
  paddingLeft: 2,
};

const searchIconContainerStyle = {
  borderRadius: 0,
  '&:hover': {
    backgroundColor: 'secondary.main',
  },
};

const searchIconStyle = { color: 'white', fontSize: '20px' };

const InputSearch = () => {
    const dispatch = useDispatch();

    const name = useSelector((state) => state.allProducts.filters.name);

    const [inputState, setInputState] = useState(name);

    useEffect(() => {
        setInputState(name);
    }, [name]);

    const handleSearchChange = debounce((e) => {
        const trimmedName = e.target.value.trim().toLowerCase();
        if (name !== trimmedName) {
            dispatch(
                setFilters({
                    pageNumber: 1,
                    name: trimmedName,
                })
            );
        }
    });

    const clearSearchInput = () => {
        dispatch(setFilters({ name: '' }));
    };

    return (
        <Box sx={inputSearchContainerStyle}>
            <Box sx={inputBaseContainerStyle}>
                <InputBase
                    fullWidth
                    sx={inputBaseStyle}
                    placeholder="Search by product name..."
                    onChange={(e) => {
                        setInputState(e.target.value);
                        handleSearchChange(e);
                    }}
                    value={inputState}
                    endAdornment={
                        name && (
                            <InputAdornment sx={{ marginRight: 1 }} position="end">
                                <IconButton onClick={clearSearchInput} edge="end">
                                    <CloseIcon />
                                </IconButton>
                            </InputAdornment>
                        )
                    }
                />
            </Box>
        </Box>
    );
};

export default InputSearch;
